import collections

def mix(s1, s2):
    def max_freq(s):
        counter = collections.Counter(s)
        return {char: max(counter[char], counter.get(char.upper(), 0)) for char in counter}

    freq_s1 = max_freq(s1.lower())
    freq_s2 = max_freq(s2.lower())

    combined_freq = {}
    for char in set(freq_s1.keys()) | set(freq_s2.keys()):
        if freq_s1[char] > 1 or freq_s2[char] > 1:
            combined_freq[char] = max(freq_s1[char], freq_s2[char])

    sorted_chars = sorted(combined_freq.items(), key=lambda item: (-item[1], item[0]))

    result_parts = []
    for char, freq in sorted_chars:
        if freq_s1[char] == freq_s2[char]:
            prefix = '=:'
        elif freq_s1[char] < freq_s2[char]:
            prefix = '2:'
        else:
            prefix = '1:'

        result_parts.append(f"{prefix}{char*freq}")

    result = '/'.join(result_parts)
    return result

