def find_largest_non_representable_number(numbers):
    numbers.sort()
    if numbers[0] >= 2:
        return 1
    max_value = sum(numbers)
    dp = [False] * (max_value + 1)
    dp[0] = True
    for num in numbers:
        for i in range(num, len(dp)):
            if dp[i - num]:
                dp[i] = True
    for i in range(max_value, -1, -1):
        if not dp[i]:
            return i
    return -1

