t = int(input())

for _ in range(t):
    keyboard = input().strip()
    s = input().strip()

    # Create a mapping from each letter to its position
    pos = {}
    for i, char in enumerate(keyboard):
        pos[char] = i

    # Calculate the total time
    total_time = 0
    for i in range(1, len(s)):
        total_time += abs(pos[s[i]] - pos[s[i-1]])

    print(total_time)

