from collections import deque

def main():
    import sys
    input = sys.stdin.read
    data = input().split()

    n = int(data[0])
    m = int(data[1])
    grid = []
    idx = 2
    for i in range(n + 1):
        row = data[idx]
        grid.append([int(c) for c in row])
        idx += 1

    dist = [[float('inf')] * (m + 1) for _ in range(n + 1)]
    q = deque()

    for i in range(n + 1):
        for j in range(m + 1):
            if grid[i][j] == 1:
                dist[i][j] = 0
                q.append((i, j))

    directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]

    while q:
        x, y = q.popleft()
        for dx, dy in directions:
            nx, ny = x + dx, y + dy
            if 0 <= nx <= n and 0 <= ny <= m and dist[nx][ny] > dist[x][y] + 1:
                dist[nx][ny] = dist[x][y] + 1
                q.append((nx, ny))

    total = 0
    for i in range(n + 1):
        for j in range(m + 1):
            total += dist[i][j] * dist[i][j]

    print(total)

if __name__ == "__main__":
    main()

