import sys

def minimal_difference(a, b, c):
    total = a + 2 * b + 3 * c
    target = total // 2
    # We try to reach as close to target as possible
    # Using 3-minute songs: they can be split as 3 * c
    # The remaining 1 and 2-minute songs can be used to fill the gap
    remaining = (a + 2 * b) % 3
    # We try to adjust the remaining songs
    if remaining == 0:
        return 0
    elif remaining == 1:
        if a > 0:
            return abs((target - (a - 1 + 2 * b + 3 * c)) - (a - 1 + 2 * b + 3 * c))
        else:
            return abs((target - (a + 2 * (b - 1) + 3 * c)) - (a + 2 * (b - 1) + 3 * c))
    else:  # remaining == 2
        if b > 0:
            return abs((target - (a + 2 * (b - 1) + 3 * c)) - (a + 2 * (b - 1) + 3 * c))
        else:
            return abs((target - (a - 2 + 2 * b + 3 * c)) - (a - 2 + 2 * b + 3 * c))

def main():
    t = int(sys.stdin.readline())
    for _ in range(t):
        a, b, c = map(int, sys.stdin.readline().split())
        print(minimal_difference(a, b, c))

if __name__ == "__main__":
    main()
