import sys

def main():
    import sys
    input = sys.stdin.read
    data = input().split()

    idx = 0
    t = int(data[idx])
    idx += 1

    results = []

    for _ in range(t):
        n = int(data[idx])
        m = int(data[idx + 1])
        idx += 2

        grid = []
        for i in range(n):
            grid.append(list(data[idx]))
            idx += 1

        min_ops = float('inf')

        # Try all possible top-left corners (i, j)
        for i in range(n):
            for j in range(m):
                # Try all possible a and b such that a >= 5, b >= 4
                for a in range(5, n - i + 1):
                    for b in range(4, m - j + 1):
                        # Check if the rectangle (i, j) to (i+a-1, j+b-1) can be a portal
                        ops = 0
                        # Check the borders
                        for x in range(i, i + a):
                            for y in [j, j + b - 1]:
                                if x == i or x == i + a - 1:
                                    # Top and bottom borders (excluding corners)
                                    if y == j or y == j + b - 1:
                                        continue  # corners can be anything
                                    if grid[x][y] != '1':
                                        ops += 1
                                else:
                                    # Left and right borders (excluding corners)
                                    if grid[x][y] != '1':
                                        ops += 1
                        # Check the inner area
                        for x in range(i + 1, i + a - 1):
                            for y in range(j + 1, j + b - 1):
                                if grid[x][y] != '0':
                                    ops += 1
                        # Update the minimum operations
                        if ops < min_ops:
                            min_ops = ops

        results.append(str(min_ops))

    print('\n'.join(results))

if __name__ == "__main__":
    main()

