t = int(input())

for _ in range(t):
    l1, l2, l3 = map(int, input().split())

    # Check if any of the sticks can be split into two parts such that
    # the four sticks can form a rectangle
    # For a rectangle, we need two pairs of equal lengths
    # So after splitting one stick into two parts, we should have two pairs

    # Try splitting l1
    for a in range(1, l1):
        b = l1 - a
        lengths = [a, b, l2, l3]
        counts = {}
        for x in lengths:
            counts[x] = counts.get(x, 0) + 1
        if sorted(counts.values()) == [2, 2]:
            print("YES")
            break
    else:
        # Try splitting l2
        for a in range(1, l2):
            b = l2 - a
            lengths = [l1, a, b, l3]
            counts = {}
            for x in lengths:
                counts[x] = counts.get(x, 0) + 1
            if sorted(counts.values()) == [2, 2]:
                print("YES")
                break
        else:
            # Try splitting l3
            for a in range(1, l3):
                b = l3 - a
                lengths = [l1, l2, a, b]
                counts = {}
                for x in lengths:
                    counts[x] = counts.get(x, 0) + 1
                if sorted(counts.values()) == [2, 2]:
                    print("YES")
                    break
            else:
                print("NO")

