    adj = defaultdict(list)
    for _ in range(n - 1):
        u, v = map(int, sys.stdin.readline().split())
        adj[u].append(v)
        adj[v].append(u)

    LOG = 20
    parent = [[-1] * LOG for _ in range(n + 1)]
    depth = [0] * (n + 1)
    path_sum = [[0] * LOG for _ in range(n + 1)]

