        for k in reversed(range(LOG)):
            if depth[u] - (1 << k) >= depth[anc]:
                res += path_sum[u][k]
                u = parent[u][k]
        return res

    def query(u, v):
        l = lca(u, v)
        return get_path_sum(u, l) + get_path_sum(v, l)

