        for k in range(LOG):
            if parent[u][k] != -1:
                path_sum[u][k] = abs(a[u]) + abs(a[parent[u][k]])
        for k in range(1, LOG):
            parent[u][k] = parent[parent[u][k - 1]][k - 1]

    for _ in range(q):
