input = sys.stdin.read

def main():
    data = input().split()
    idx = 0
    t = int(data[idx])
    idx += 1
    results = []

    for _ in range(t):
        n = int(data[idx])
        m = int(data[idx+1])
        idx += 2
        grid = []
        for _ in range(n):
            row = data[idx]
            grid.append([int(c) for c in row])
            idx += 1

        min_ops = float('inf')

        for a in range(5, n + 1):
            for b in range(4, m + 1):
                for i in range(n - a + 1):
                    for j in range(m - b + 1):
                        ops = 0

                        # Top row
                        for col in range(j, j + b):
                            if grid[i][col] != 1:
                                ops += 1

                        # Bottom row
                        for col in range(j, j + b):
                            if grid[i + a - 1][col] != 1:
                                ops += 1

                        # Left column
                        for row in range(i + 1, i + a - 1):
                            if grid[row][j] != 1:
                                ops += 1

                        # Right column
                        for row in range(i + 1, i + a - 1):
                            if grid[row][j + b - 1] != 1:
                                ops += 1

                        # Interior
                        for row in range(i + 1, i + a - 1):
                            for col in range(j + 1, j + b - 1):
                                if grid[row][col] != 0:
                                    ops += 1

                        min_ops = min(min_ops, ops)

        results.append(str(min_ops))

    print("\n".join(results))

