def is_valid(s):
    for i in range(len(s)):
        if s[i] == s[(i + 1) % len(s)]:
            return False
    return True

def main():
    import sys
    input = sys.stdin.read
    data = input().split()

    t = int(data[0])
    results = []
    index = 1

    for _ in range(t):
        s = data[index]
        index += 1
        results.append("YES" if is_valid(s) else "NO")

    for result in results:
        print(result)

if __name__ == "__main__":
    main()
