from math import factorial

def mod_inv(a, m):
    return pow(a, m - 2, m)

def nCr(n, r, mod):
    return (factorial(n) * mod_inv(factorial(r), mod) % mod * mod_inv(factorial(n - r), mod) % mod) % mod

def main():
    import sys
    input = sys.stdin.read
    data = input().split()

    index = 0
    T = int(data[index])
    index += 1

    results = []
    for _ in range(T):
        n = int(data[index])
        m = int(data[index + 1])
        k = int(data[index + 2])
        p = int(data[index + 3])
        index += 4

        MOD = p

        if k == 1:
            result = nCr(n, m, MOD)
        elif k == n:
            result = 1
        else:
            result = 0

        results.append(result)

    for result in results:
        print(result)

if __name__ == "__main__":
    main()

