def min_difference(a, b, c):
    total = a + b * 2 + c * 3
    target = total // 2
    dp = [float('inf')] * (target + 1)
    dp[0] = 0

    for i in range(a):
        dp[i] = 1

    for i in range(b):
        for j in range(target, i, -1):
            dp[j] = min(dp[j], dp[j - i] + 1)

    for i in range(c):
        for j in range(target, i * 2, -1):
            dp[j] = min(dp[j], dp[j - i * 2] + 1)

    return abs(total - 2 * dp[target])

