import itertools

def is_composite(num):
    if num <= 1:
        return False
    for i in range(2, int(num ** 0.5) + 1):
        if num % i == 0:
            return True
    return False

def largest_subset_with_composite_sum(n, arr):
    max_size = 0
    best_subset = []

    for r in range(3, n + 1):  # Start from 3 to ensure we find a subset with at least 3 elements
        for subset in itertools.combinations(arr, r):
            if is_composite(sum(subset)):
                if len(subset) > max_size:
                    max_size = len(subset)
                    best_subset = sorted(subset)

    return max_size, best_subset

def main():
    import sys
    input = sys.stdin.read
    data = input().split()

    index = 0
    t = int(data[index])
    index += 1

    results = []
    for _ in range(t):
        n = int(data[index])
        index += 1
        arr = list(map(int, data[index:index + n]))
        index += n

        max_size, best_subset = largest_subset_with_composite_sum(n, arr)
        results.append((max_size, best_subset))

    for max_size, best_subset in results:
        print(max_size)
        print(' '.join(str(i + 1) for i in best_subset))

# Call the main function
if __name__ == "__main__":
    main()
