    balance = 0
    for char in s:
        if char == '(':
            balance += 1
        else:
            balance -= 1
        if balance < 0:
            return False
    return balance == 0

def max_rbs_prefixes(n, sequences):
    total_length = sum(len(seq) for seq in sequences)
    max_rbs = 0

    def backtrack(index, current, rbs_count):
        nonlocal max_rbs
        if index == n:
            max_rbs = max(max_rbs, rbs_count)
            return

        for i in range(n):
            if sequences[i]:
