def get_score(dice):
    counts = Counter(dice)
    total_points = 0

    # Check for straight (1,2,3,4,5,6)
    if sorted(dice) == [1, 2, 3, 4, 5, 6]:
        return 1000

    # Check for three pairs
    if len(counts) == 3 and all(count == 2 for count in counts.values()):
        return 750

    # Make a copy to work with
    working_counts = counts.copy()

    # Check for N of a kind (N >= 3)
    for num in range(1, 7):
        count = working_counts[num]
        if count >= 3:
            base_points = {1: 1000, 2: 200, 3: 300, 4: 400, 5: 500, 6: 600}
            if count == 3:
                total_points += base_points[num]
                working_counts[num] -= 3
            elif count == 4:
                total_points += base_points[num] * 2
                working_counts[num] -= 4
            elif count == 5:
                total_points += base_points[num] * 3
                working_counts[num] -= 5
            elif count == 6:
                total_points += base_points[num] * 4
                working_counts[num] -= 6

    # Score remaining 1s and 5s
    total_points += working_counts[1] * 100
    total_points += working_counts[5] * 50

    # Check if we scored anything
    if total_points == 0:
        return "Zonk"

    return total_points

# Read input and process
if __name__ == "__main__":
    dice_input = eval(input())
    result = get_score(dice_input)
    print(result)

def expanded_form(num):
    if num == 0:
        return ""

    num_str = str(num)
    length = len(num_str)
    result = []

    for i, digit in enumerate(num_str):
        if digit != '0':
            power = length - i - 1
            if power == 0:
                result.append(digit)
            elif power == 1:
                result.append(f"{digit}*10")
            else:
                result.append(f"{digit}*{'1' + '0' * power}")

    return "+".join(result)

# Read input and print output
num = int(input())
print(expanded_form(num))

