def alphabet_war(fight):
    left_power = {'w': 4, 'p': 3, 'b': 2, 's': 1}
    right_power = {'m': 4, 'q': 3, 'd': 2, 'z': 1}

    # Handle empty string
    if not fight:
        return "Let's fight again!"

    # Convert string to list for easier manipulation
    chars = list(fight)
    n = len(chars)

    # Mark positions to be removed due to bomb explosions
    to_remove = [False] * n

    # Process bombs and mark affected positions
    for i in range(n):
        if chars[i] == '*':
            # Mark the bomb position itself
            to_remove[i] = True
            # Mark left neighbor if exists
            if i > 0:
                to_remove[i-1] = True
            # Mark right neighbor if exists
            if i < n-1:
                to_remove[i+1] = True

    # Calculate powers of surviving letters
    left_sum = 0
    right_sum = 0

    for i in range(n):
        if not to_remove[i]:  # If character survives
            char = chars[i]
            if char in left_power:
                left_sum += left_power[char]
            elif char in right_power:
                right_sum += right_power[char]

    # Determine winner
    if left_sum > right_sum:
        return "Left side wins!"
    elif right_sum > left_sum:
        return "Right side wins!"
    else:
        return "Let's fight again!"

# Read input and execute
fight = input().strip()
print(alphabet_war(fight))

