import sys

def getAllPrimeFactors(n):
    # Input validation
    if not isinstance(n, int) or n <= 0:
        return []

    # Edge cases
    if n == 1:
        return [1]

    factors = []
    divisor = 2

    while divisor * divisor <= n:
        while n % divisor == 0:
            factors.append(divisor)
            n //= divisor
        divisor += 1

    if n > 1:
        factors.append(n)

    return factors

def getUniquePrimeFactorsWithCount(n):
    # Input validation
    if not isinstance(n, int) or n <= 0:
        return [[], []]

    # Edge cases
    if n == 1:
        return [[1], [1]]

    factors = getAllPrimeFactors(n)
    if not factors:
        return [[], []]

    unique_primes = []
    counts = []

    for factor in factors:
        if not unique_primes or factor != unique_primes[-1]:
            unique_primes.append(factor)
            counts.append(1)
        else:
            counts[-1] += 1

    return [unique_primes, counts]

def getUniquePrimeFactorsWithProducts(n):
    # Input validation
    if not isinstance(n, int) or n <= 0:
        return []

    # Edge cases
    if n == 1:
        return [1]

    primes, counts = getUniquePrimeFactorsWithCount(n)
    if not primes:
        return []

    products = []
    for i in range(len(primes)):
        products.append(primes[i] ** counts[i])

    return products

# Read input and process
try:
    n = int(input())
    result = getAllPrimeFactors(n)
    print(result)
except (ValueError, TypeError):
    print([])

