def transform_string(s):
    vowels = set('aeiou')
    exceptions = {'c': -1, 'o': -1, 'd': -3, 'e': -4}
    revert_set = set('code')

    def move_char(c, shift):
        # Calculate new position with proper wrapping
        new_pos = (ord(c) - ord('a') + shift) % 26
        return chr(new_pos + ord('a'))

    result = []
    for char in s:
        original_char = char
        if char in exceptions:
            new_char = move_char(char, exceptions[char])
        elif char in vowels:
            new_char = move_char(char, -5)
        else:  # consonant
            new_char = move_char(char, 9)

        # If the transformed character is in revert set, revert to original
        if new_char in revert_set:
            new_char = original_char
        result.append(new_char)

    return ''.join(result)

s = input().strip()
print(transform_string(s))

