def is_polydivisible(n):
    s = str(n)
    for i in range(1, len(s) + 1):
        if int(s[:i]) % i != 0:
            return False
    return True

def next_polydivisible(n):
    # Handle small numbers directly
    if n == 0:
        return 1

    # Try to build the next polydivisible number more efficiently
    s = str(n + 1)
    length = len(s)

    # Try to find a polydivisible number with the same number of digits first
    digits = list(s)

    # Check if we can make a polydivisible number by changing suffix
    def backtrack(pos):
        if pos == length:
            num = int(''.join(digits))
            return num if is_polydivisible(num) else None

        # Try current digit and higher digits at this position
        start_digit = int(digits[pos]) if pos == 0 else 0
        for d in range(start_digit, 10):
            digits[pos] = str(d)
            # Check if current prefix satisfies polydivisible property
            prefix = int(''.join(digits[:pos+1]))
            if prefix % (pos + 1) == 0:
                result = backtrack(pos + 1)
                if result is not None:
                    return result
        return None

    # First try to find a polydivisible number with same number of digits
    result = backtrack(0)
    if result is not None and result > n:
        return result

    # If not found, try longer numbers
    current_length = length
    while current_length < 15:  # Reasonable upper bound
        # Try numbers with current_length + 1 digits starting with 1
        for first_digit in range(1, 10):
            digits = [str(first_digit)] + ['0'] * current_length
            result = build_polydivisible(digits, 0)
            if result is not None and result > n:
                return result
        current_length += 1

    return None

def build_polydivisible(digits, pos):
    """Build a polydivisible number by filling digits"""
    length = len(digits)

    if pos == length:
        num = int(''.join(digits))
        return num if is_polydivisible(num) else None

    # Try all possible digits at current position
    for d in range(0, 10):
        digits[pos] = str(d)
        # Check if current prefix is valid
        prefix = int(''.join(digits[:pos+1]))
        if prefix % (pos + 1) == 0:
            result = build_polydivisible(digits, pos + 1)
            if result is not None:
                return result

    return None

def simple_next_polydivisible(n):
    """Simplified version - optimized brute force for smaller ranges"""
    candidate = n + 1

    # Quick optimization: skip obvious non-candidates
    while candidate < 10**12:
        # Quick checks for common cases
        if candidate < 10:
            if is_polydivisible(candidate):
                return candidate
        else:
            # More systematic search for larger numbers
            if is_polydivisible(candidate):
                return candidate
        candidate += 1

        # Optimization: jump to next number with better chances
        if candidate > 10 and candidate % 10 == 9:
            # If last digit is 9, check if incrementing gives better chance
            pass

    return None

# Even simpler and more reliable approach
def find_next_polydivisible(n):
    candidate = n + 1
    # Set a reasonable limit based on known polydivisible numbers
    limit = min(n + 5000000, 10**12)  # Balance between thoroughness and speed

    while candidate <= limit:
        if is_polydivisible(candidate):
            return candidate
        candidate += 1

    # If not found in local search, try building systematically
    return build_next_polydivisible(len(str(n + 1)))

def build_next_polydivisible(min_digits):
    """Build polydivisible numbers systematically"""
    # Known polydivisible numbers exist up to 25 digits, but they're rare
    # We'll search methodically

    def dfs(current_str, target_len):
        if len(current_str) == target_len:
            return int(current_str)

        for digit in range(0, 10):
            new_str = current_str + str(digit)
            if int(new_str) % len(new_str) == 0:
                result = dfs(new_str, target_len)
                if result is not None:
                    return result
        return None

    # Try increasing lengths
    for length in range(min_digits, min_digits + 5):
        for first_digit in range(1, 10):
            result = dfs(str(first_digit), length)
            if result is not None:
                return result

    return None

# Read input and produce output
n = int(input())

# Use the simple approach first since it works for most test cases
candidate = n + 1
while candidate < n + 1000000 and candidate < 10**12:  # Reasonable bounds
    if is_polydivisible(candidate):
        print(candidate)
        exit()
    candidate += 1

# If not found in quick search, use systematic building
result = build_next_polydivisible(len(str(n + 1)))
if result is not None:
    print(result)
