def main():
    n, k = map(int, input().split())
    grid = []
    for _ in range(4):
        grid.append(list(map(int, input().split())))

    # Parse initial positions and target positions
    initial_pos = {}
    target_pos = {}
    for i in range(4):
        for j in range(n):
            val = grid[i][j]
            if val != 0:
                if i == 0 or i == 3:  # Target positions (rows 1 and 4 in 0-indexed)
                    target_pos[val] = (i, j)
                else:  # Initial positions (rows 2 and 3 in 0-indexed)
                    initial_pos[val] = (i, j)

    # Check if already solved
    solved = True
    for car in range(1, k+1):
        if initial_pos[car] != target_pos[car]:
            solved = False
            break

    if solved:
        print(0)
        return

    # BFS with state compression
    def state_key(pos_dict):
        return tuple(sorted((car, pos) for car, pos in pos_dict.items()))

    queue = deque([(initial_pos.copy(), [])])
    visited = {state_key(initial_pos)}

    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]

    while queue:
        pos_dict, moves = queue.popleft()

        if len(moves) >= 20000:
            continue

        # Create current grid state
        current_grid = [[0] * n for _ in range(4)]
        for car, (r, c) in pos_dict.items():
            current_grid[r][c] = car

        # Find all empty spaces
        empty_spaces = set()
        for r in range(4):
            for c in range(n):
                if current_grid[r][c] == 0:
                    empty_spaces.add((r, c))

        # For each empty space, try to move adjacent cars into it
        for er, ec in empty_spaces:
            # Check all 4 directions for cars that can move here
            for dr, dc in directions:
                nr, nc = er + dr, ec + dc

                # Check bounds
                if not (0 <= nr < 4 and 0 <= nc < n):
                    continue

                car = current_grid[nr][nc]
                if car == 0:  # No car here
                    continue

                # Check if this is a valid move
                valid_move = True
                if er == 0 or er == 3:  # Moving to first or fourth row
                    if (er, ec) != target_pos[car]:  # Must be the car's designated spot
                        valid_move = False

                if valid_move:
                    # Make the move
                    new_pos_dict = pos_dict.copy()
                    new_pos_dict[car] = (er, ec)

                    new_state = state_key(new_pos_dict)
                    if new_state not in visited:
                        visited.add(new_state)
                        new_moves = moves + [(car, er + 1, ec + 1)]  # Convert to 1-indexed

                        # Check if goal reached
                        goal_reached = True
                        for c_id in range(1, k + 1):
                            if new_pos_dict[c_id] != target_pos[c_id]:
                                goal_reached = False
                                break

                        if goal_reached:
                            print(len(new_moves))
                            for move in new_moves:
                                print(move[0], move[1], move[2])
                            return

                        if len(new_moves) < 20000:
                            queue.append((new_pos_dict, new_moves))

    print(-1)

if __name__ == "__main__":
    main()

