def solve(s):
    vowels = "aeiou"
    vowel_list = [c for c in s if c in vowels]
    consonant_list = [c for c in s if c not in vowels]

    v_count = len(vowel_list)
    c_count = len(consonant_list)

    # Check if alternating is possible
    if abs(v_count - c_count) > 1:
        return "failed"

    # Handle edge cases
    if v_count == 0 and c_count == 0:
        return ""

    if v_count == 0 or c_count == 0:
        if v_count + c_count <= 1:
            return s
        else:
            return "failed"

    # Sort the characters
    vowel_list.sort()
    consonant_list.sort()

    # Build the result based on counts
    if v_count == c_count:
        # Must start with vowel
        result = []
        for i in range(v_count):
            result.append(vowel_list[i])
            result.append(consonant_list[i])
        return ''.join(result)
    elif v_count == c_count + 1:
        # Start with vowel (one extra vowel at end)
        result = []
        for i in range(c_count):
            result.append(vowel_list[i])
            result.append(consonant_list[i])
        result.append(vowel_list[-1])
        return ''.join(result)
    elif c_count == v_count + 1:
        # Start with consonant (one extra consonant at end)
        result = []
        for i in range(v_count):
            result.append(consonant_list[i])
            result.append(vowel_list[i])
        result.append(consonant_list[-1])
        return ''.join(result)

    return "failed"

