import math

def factors(n):
    # Validate input
    if not isinstance(n, int) or n < 1:
        return -1

    factors_list = []

    # Find factors up to sqrt(n)
    for i in range(1, int(math.sqrt(n)) + 1):
        if n % i == 0:
            factors_list.append(i)
            # Add the corresponding factor (n // i) if it's not a duplicate
            if i != n // i:
                factors_list.append(n // i)

    # Sort in descending order
    factors_list.sort(reverse=True)

    return factors_list

