def is_prime(num):
    if num < 2:
        return False
    if num == 2:
        return True
    if num % 2 == 0:
        return False
    for i in range(3, int(num**0.5) + 1, 2):
        if num % i == 0:
            return False
    return True

def is_mersenne_prime(n):
    if n <= 0:
        return False

    # First check if n is prime
    if not is_prime(n):
        return False

    # Calculate 2^n - 1
    mersenne_num = (1 << n) - 1  # Bit shifting for 2^n

    # Check if the Mersenne number is prime
    return is_prime(mersenne_num)

