import math
from functools import reduce

def gcd_list(nums):
    return reduce(math.gcd, nums)

def solve(nums):
    # 如果列表为空，返回0
    if not nums:
        return 0

    # 计算所有数字的最大公约数
    gcd_all = gcd_list(nums)

    # 如果GCD大于1，则有无限多个无法表示的数
    if gcd_all > 1:
        return -1

    # 如果1在列表中，则所有正整数都可以表示
    if 1 in nums:
        return 0

    # 对于两个数的情况，使用公式
    if len(nums) == 2:
        a, b = sorted(nums)
        return a * b - a - b

    # 对于更多数字的情况，使用动态规划
    # 使用更合理的上界估计
    max_num = max(nums)
    # Frobenius数的一个上界估计
    limit = max_num * max_num

    # 确保有一个合理的上限以避免内存问题
    limit = min(limit, 1000000)

    # dp[i] 表示数字 i 是否可以被表示
    dp = [False] * (limit + 1)
    dp[0] = True

    # 标记所有可以表示的数字
    for i in range(limit + 1):
        if dp[i]:
            for num in nums:
                if i + num <= limit:
                    dp[i + num] = True

    # 找到最大的不能表示的数字
    # 从后往前找，找到第一个不能表示的数
    for i in range(limit, -1, -1):
        if not dp[i]:
            return i

    # 理论上不应该到达这里
    return 0

# 读取输入并解析
try:
    input_str = input().strip()
    if input_str.startswith('[') and input_str.endswith(']'):
        content = input_str[1:-1].strip()
        if content:
            nums = list(map(int, content.split(',')))
        else:
            nums = []
    else:
        nums = [int(input_str)]
except:
    nums = []

# 求解并输出结果
result = solve(nums)
print(result)

