def next_smaller(n):
    digits = list(str(n))
    length = len(digits)

    # Step 1: Find the first digit from the right that is greater than its right neighbor
    pivot = -1
    for i in range(length - 2, -1, -1):
        if digits[i] > digits[i + 1]:
            pivot = i
            break

    # If no such digit is found, no smaller permutation exists
    if pivot == -1:
        return -1

    # Step 2: Find the largest digit to the right of pivot that is smaller than digits[pivot]
    successor = -1
    for i in range(length - 1, pivot, -1):
        if digits[i] < digits[pivot]:
            if successor == -1 or digits[i] > digits[successor]:
                successor = i

    # Step 3: Swap the pivot and successor
    digits[pivot], digits[successor] = digits[successor], digits[pivot]

    # Step 4: Sort the suffix (everything after pivot) in descending order
    digits[pivot + 1:] = sorted(digits[pivot + 1:], reverse=True)

    # Step 5: Check for leading zeros
    if digits[0] == '0':
        return -1

    return int(''.join(digits))

# Read input and print output
