def find(parent, x):
    if parent[x] != x:
        parent[x] = find(parent, parent[x])
    return parent[x]

def union(parent, rank, x, y):
    px, py = find(parent, x), find(parent, y)
    if px == py:
        return False
    if rank[px] < rank[py]:
        px, py = py, px
    parent[py] = px
    if rank[px] == rank[py]:
        rank[px] += 1
    return True

def main():
    n, m, a, b = map(int, input().split())
    edges_a = []  # edges with weight a
    edges_b = []  # edges with weight b

    for _ in range(m):
        u, v, c = map(int, input().split())
        if c == a:
            edges_a.append((u, v))
        else:
            edges_b.append((u, v))

    # Kruskal's algorithm to build MST
    parent = list(range(n + 1))
    rank = [0] * (n + 1)
    mst_edges = []

    # Function to check if a node is in the same component as node 1
    def connected_to_one(u, v):
        return find(parent, u) == find(parent, 1) or find(parent, v) == find(parent, 1)

    # Process edges with weight a
    # Sort edges: prioritize those connecting to component with node 1
    edges_a.sort(key=lambda edge: not connected_to_one(edge[0], edge[1]))

    for u, v in edges_a:
        if union(parent, rank, u, v):
            mst_edges.append((u, v, a))

    # Process edges with weight b
    # Sort edges: prioritize those connecting to component with node 1
    edges_b.sort(key=lambda edge: not connected_to_one(edge[0], edge[1]))

    for u, v in edges_b:
        if union(parent, rank, u, v):
            mst_edges.append((u, v, b))

    # Build adjacency list for MST
    adj = [[] for _ in range(n + 1)]
    for u, v, c in mst_edges:
        adj[u].append((v, c))
        adj[v].append((u, c))

    # BFS to find shortest distances from node 1
    dist = [-1] * (n + 1)
    dist[1] = 0
    queue = [1]

    while queue:
        u = queue.pop(0)
        for v, c in adj[u]:
            if dist[v] == -1:
                dist[v] = dist[u] + c
                queue.append(v)

    # Output results
    print(' '.join(str(dist[i]) for i in range(1, n + 1)))

if __name__ == "__main__":
    main()

