import math

def is_perfect_square(x):
    if x < 0:
        return False
    if x == 0:
        return True
    # Use integer square root to avoid floating point issues
    root = int(math.isqrt(x))  # Available in Python 3.8+
    return root * root == x

def sum_of_squares(n):
    # Check if n is a perfect square
    if is_perfect_square(n):
        return 1

    # Check if n is of the form 4^a * (8b + 7)
    temp = n
    while temp % 4 == 0:
        temp //= 4
    if temp % 8 == 7:
        return 4

    # Check if n can be expressed as the sum of two squares
    for i in range(1, int(math.isqrt(n)) + 1):
        remainder = n - i * i
        if is_perfect_square(remainder):
            return 2

    # If none of the above, it must be expressible as the sum of three squares
    return 3

n = int(input())
print(sum_of_squares(n))

