def proper_fractions(d):
    if d == 1:
        return 0

    result = d
    n = d  # Work with a copy to preserve original d for calculations

    # Check for factor 2
    if d % 2 == 0:
        result = result // 2
        while n % 2 == 0:
            n = n // 2

    # Check for odd factors from 3 onwards
    i = 3
    while i * i <= n:
        if n % i == 0:
            result = result - result // i
            while n % i == 0:
                n = n // i
        i += 2

    # If n is still greater than 1, then it's a prime factor
    if n > 1:
        result = result - result // n

    return result

