def differentiate(poly, x_val):
    if not poly:
        return 0

    # Split the polynomial into terms
    terms = []
    current_term = ""

    for i, char in enumerate(poly):
        if char in '+-' and i > 0:  # Don't split on the first character if it's a sign
            terms.append(current_term)
            current_term = char
        else:
            current_term += char
    terms.append(current_term)

    result = 0

    for term in terms:
        # Handle the sign
        sign = 1
        if term.startswith('-'):
            sign = -1
            term = term[1:]
        elif term.startswith('+'):
            term = term[1:]

        # Parse coefficient and exponent
        coeff = 1
        exp = 0

        if 'x' in term:
            if term == 'x':
                coeff = 1
                exp = 1
            else:
                if '^' in term:
                    # Has exponent
                    parts = term.split('x^')
                    if parts[0] == '':
                        coeff = 1
                    else:
                        coeff = int(parts[0])
                    exp = int(parts[1])
                else:
                    # No exponent (implies ^1)
                    coeff_part = term[:-1]  # Remove 'x'
                    if coeff_part == '':
                        coeff = 1
                    else:
                        coeff = int(coeff_part)
                    exp = 1
        else:
            # Constant term
            if term:
                coeff = int(term)
            exp = 0

        # Apply differentiation rule: d/dx(ax^n) = n*a*x^(n-1)
        if exp == 0:
            # Derivative of constant is 0
            deriv_value = 0
        else:
            new_coeff = exp * coeff
            new_exp = exp - 1
            if new_exp == 0:
                # x^0 = 1, so just the coefficient
                deriv_value = new_coeff
            else:
                # Evaluate at x
                deriv_value = new_coeff * (x_val ** new_exp)

        result += sign * deriv_value

    return result

# Read inputs
poly = input().strip()
x_val = int(input().strip())

# Calculate and print result
print(differentiate(poly, x_val))

