import sys
from functools import lru_cache

def solve():
    t = int(input())

    for _ in range(t):
        n = int(input())
        a = list(map(int, input().split()))

        @lru_cache(maxsize=None)
        def can_win(state, last_pile):
            # state is a tuple of pile sizes
            # last_pile is the index of the last pile chosen (-1 if none)

            # Try all possible moves
            for i in range(n):
                # Can't choose empty pile or the same pile as last turn
                if state[i] > 0 and i != last_pile:
                    # Make a move: remove one stone from pile i
                    new_state = list(state)
                    new_state[i] -= 1
                    # If opponent cannot win after our move, we win
                    if not can_win(tuple(new_state), i):
                        return True
            # If no winning move found, current player loses
            return False

        initial_state = tuple(a)
        if can_win(initial_state, -1):
            print("T")
        else:
            print("HL")

solve()

