def words_to_number(s):
    # Define mappings
    units = {
        "zero": 0, "one": 1, "two": 2, "three": 3, "four": 4,
        "five": 5, "six": 6, "seven": 7, "eight": 8, "nine": 9
    }

    teens = {
        "ten": 10, "eleven": 11, "twelve": 12, "thirteen": 13, "fourteen": 14,
        "fifteen": 15, "sixteen": 16, "seventeen": 17, "eighteen": 18, "nineteen": 19
    }

    tens = {
        "twenty": 20, "thirty": 30, "forty": 40, "fifty": 50,
        "sixty": 60, "seventy": 70, "eighty": 80, "ninety": 90
    }

    scales = {
        "hundred": 100,
        "thousand": 1000,
        "million": 1000000
    }

    # Normalize the input
    s = s.strip().lower()
    # Handle hyphens by replacing them with spaces
    s = s.replace('-', ' ')
    words = [word for word in s.split() if word != 'and']

    if not words:
        return 0

    result = 0
    current_number = 0

    i = 0
    while i < len(words):
        word = words[i]

        if word in units:
            current_number += units[word]
        elif word in teens:
            current_number += teens[word]
        elif word in tens:
            current_number += tens[word]
        elif word in scales:
            scale_value = scales[word]
            if word == "hundred":
                current_number *= scale_value
            else:  # thousand or million
                result += current_number * scale_value
                current_number = 0
        i += 1

    result += current_number
    return result

# Read input and process
s = input()
print(words_to_number(s))

