import sys

def main():
    MOD = 10**9 + 7

    n, m = map(int, input().split())
    grid = [input() for _ in range(n)]

    # Precompute whether we can move right or down from each cell
    can_right = [[False]*m for _ in range(n)]
    can_down = [[False]*m for _ in range(n)]

    # For each cell, determine if we can move right
    for i in range(n):
        # Process from right to left
        for j in range(m-2, -1, -1):
            # Check if we can move right from (i, j)
            if grid[i][j+1] == '.':
                # No rock to the right, just move normally
                can_right[i][j] = True
            else:
                # There's a rock to the right, need to push it
                # Find the end of the rock chain
                k = j + 1
                while k < m and grid[i][k] == 'R':
                    k += 1
                # If we found an empty spot, move is possible
                can_right[i][j] = (k < m)

    # For each cell, determine if we can move down
    for j in range(m):
        # Process from bottom to top
        for i in range(n-2, -1, -1):
            # Check if we can move down from (i, j)
            if grid[i+1][j] == '.':
                # No rock below, just move normally
                can_down[i][j] = True
            else:
                # There's a rock below, need to push it
                # Find the end of the rock chain
                k = i + 1
                while k < n and grid[k][j] == 'R':
                    k += 1
                # If we found an empty spot, move is possible
                can_down[i][j] = (k < n)

    # Special case: if goal cell has a rock that can't be pushed out, it's blocked
    if grid[n-1][m-1] == 'R':
        # Check if we can push it out
        if n == 1:
            # In a single row, check if there's space to the right
            i = m-1
            while i >= 0 and grid[0][i] == 'R':
                i -= 1
            if i < 0:
                print(0)
                return
        else:
            # Check if we can push it down
            i = n-1
            while i >= 0 and grid[i][m-1] == 'R':
                i -= 1
            if i < 0:
                print(0)
                return

    # DP to count paths
    dp = [[0]*m for _ in range(n)]
    dp[0][0] = 1

    for i in range(n):
        for j in range(m):
            if dp[i][j] == 0:
                continue

            # Move right
            if j + 1 < m and can_right[i][j]:
                dp[i][j + 1] = (dp[i][j + 1] + dp[i][j]) % MOD

            # Move down
            if i + 1 < n and can_down[i][j]:
                dp[i + 1][j] = (dp[i + 1][j] + dp[i][j]) % MOD

    print(dp[n - 1][m - 1])

if __name__ == "__main__":
    main()

