import sys
from math import gcd
from functools import reduce

def gcd_list(lst):
    return reduce(gcd, lst)

def solve(numbers):
    # Handle edge cases
    if 1 in numbers:
        return 0

    # Check if GCD of all numbers is greater than 1
    if gcd_list(numbers) > 1:
        return -1

    # For single number > 1, infinite non-representable numbers
    if len(numbers) == 1:
        return -1

    # For two coprime numbers, use the formula
    if len(numbers) == 2:
        a, b = numbers[0], numbers[1]
        if gcd(a, b) == 1:
            return a * b - a - b

    # For more than 2 numbers, use DP approach
    max_num = max(numbers)
    # Set a reasonable upper bound - Frobenius number is bounded
    limit = max_num * max_num

    # DP array to mark which numbers can be formed
    dp = [False] * (limit + 1)
    dp[0] = True

    # Fill the dp array
    for i in range(limit + 1):
        if dp[i]:
            for num in numbers:
                if i + num <= limit:
                    dp[i + num] = True

    # Find the point after which all numbers can be represented
    # Look for max_num consecutive true values
    consecutive_count = 0

    # Start from the end and work backwards
    for i in range(limit, -1, -1):
        if dp[i]:
            consecutive_count += 1
        else:
            consecutive_count = 0

        if consecutive_count == max_num:
            # All numbers from i to infinity can be represented
            # Find the largest non-representable number before i
            for j in range(i - 1, -1, -1):
                if not dp[j]:
                    return j
            return 0

    # If we didn't find enough consecutive numbers, search from the beginning
    for i in range(limit, -1, -1):
        if not dp[i]:
            return i

    return 0

# Read input
line = input().strip()
numbers = eval(line)

# Solve and print result
result = solve(numbers)
print(result)

