import sys
from collections import defaultdict

def prime_factorize(n):
    factors = {}
    d = 2
    while d * d <= n:
        while n % d == 0:
            if d not in factors:
                factors[d] = 0
            factors[d] += 1
            n //= d
        d += 1
    if n > 1:
        factors[n] = 1
    return factors

def modinv(a, mod):
    return pow(a, mod - 2, mod)

def solve(n, k):
    MOD = 1000000007

    if n == 1:
        return 1

    # Get prime factorization
    factors = prime_factorize(n)

    # For each prime p with exponent e, we track the probability distribution
    # of the exponent after k steps
    result = 1
    for prime, exp in factors.items():
        # For each prime, compute the probability distribution of exponents after k steps
        dp = [0] * (exp + 1)
        dp[exp] = 1  # Start with full exponent

        for step in range(k):
            new_dp = [0] * (exp + 1)
            for i in range(exp + 1):
                if dp[i] == 0:
                    continue
                # When exponent is i, we can choose any value from 0 to i with equal probability
                count = i + 1
                prob = dp[i] * modinv(count, MOD) % MOD
                for j in range(i + 1):
                    new_dp[j] = (new_dp[j] + prob) % MOD
            dp = new_dp

        # Compute expected value of prime^exponent
        expected_value = 0
        power_of_prime = 1
        for i in range(exp + 1):
            expected_value = (expected_value + power_of_prime * dp[i]) % MOD
            power_of_prime = (power_of_prime * prime) % MOD

        result = (result * expected_value) % MOD

    return result

def main():
    line = input().strip()
    n, k = map(int, line.split())
    print(solve(n, k))

if __name__ == "__main__":
    main()

