def count_distinct_cyclic_shifts(s):
    distinct_strings = set()
    current = s
    while True:
        distinct_strings.add(current)
        # Perform cyclic shift: move last character to front
        current = current[-1] + current[:-1]
        if current == s:
            break
    return len(distinct_strings)

# Read input and solve
s = input().strip()
print(count_distinct_cyclic_shifts(s))

