def is_palindrome(s):
    return s == s[::-1]

def is_quasi_palindromic(x_str):
    # Check if original string is palindromic
    if is_palindrome(x_str):
        return "YES"

    # Try adding up to 20 leading zeros (more than enough for the constraint)
    for i in range(1, 21):
        candidate = '0' * i + x_str
        if is_palindrome(candidate):
            return "YES"

    return "NO"

x = input().strip()
print(is_quasi_palindromic(x))

