def generate_valid_numbers():
    valid_numbers = []
    # For each possible number of bits (from 2 to 64)
    for bits in range(2, 65):
        # For each possible position of the zero (from 0 to bits-2, since the MSB cannot be zero)
        for zero_pos in range(bits - 1):
            # Create a number with all ones except a zero at position zero_pos
            # Start with all ones: (1<<bits) - 1
            # Then subtract (1<<zero_pos) to make that bit zero
            num = ((1 << bits) - 1) - (1 << zero_pos)
            if num <= 10**18:
                valid_numbers.append(num)

    return sorted(list(set(valid_numbers)))

def count_in_range(valid_numbers, a, b):
    # Binary search for lower bound (first number >= a)
    left, right = 0, len(valid_numbers)
    while left < right:
        mid = (left + right) // 2
        if valid_numbers[mid] >= a:
            right = mid
        else:
            left = mid + 1

    # Binary search for upper bound (first number > b)
    left2, right2 = 0, len(valid_numbers)
    while left2 < right2:
        mid = (left2 + right2) // 2
        if valid_numbers[mid] > b:
            right2 = mid
        else:
            left2 = mid + 1

    return left2 - left

valid_numbers = generate_valid_numbers()
a, b = map(int, input().split())
print(count_in_range(valid_numbers, a, b))

