def count_safe_codes(s):
    # Track which letters are mapped to which digits (we don't actually need the mapping, just that it's assigned)
    letter_assigned = {}
    # Track how many digits have been used for letters
    digits_used_for_letters = 0
    total_combinations = 1

    for i, char in enumerate(s):
        if char.isdigit():
            # Fixed digit, no choice
            continue
        elif char == '?':
            # Wildcard: 10 choices (0-9), but check first character constraint
            if i == 0:
                total_combinations *= 9  # Can't be 0 for first position
            else:
                total_combinations *= 10
        else:
            # Letter from A-J
            if char in letter_assigned:
                # Already assigned, no additional choice
                continue
            else:
                # First occurrence of this letter
                if i == 0:
                    # First character: can't be 0
                    # We have 10 total digits (0-9) minus 1 (can't use 0) minus digits already used for other letters
                    remaining_choices = 9 - digits_used_for_letters
                    total_combinations *= remaining_choices
                else:
                    # Can use any digit not yet used for letters
                    # We have 10 digits minus digits already used for other letters
                    remaining_choices = 10 - digits_used_for_letters
                    total_combinations *= remaining_choices

                # Mark this letter as assigned and increment the count of used digits
                letter_assigned[char] = True
                digits_used_for_letters += 1

    return total_combinations

# Read input and execute
s = input().strip()
print(count_safe_codes(s))

