def solve():
    n, m = map(int, input().split())
    grid = []
    for _ in range(n):
        row = list(map(int, input().split()))
        grid.append(row)

    # Create position map: number -> [pos1, pos2]
    pos_map = {}
    for i in range(n):
        for j in range(m):
            num = grid[i][j]
            if num not in pos_map:
                pos_map[num] = []
            pos_map[num].append((i, j))

    # Find all mismatched pairs (pairs that are not adjacent)
    mismatched = []
    for num, positions in pos_map.items():
        p1, p2 = positions
        i1, j1 = p1
        i2, j2 = p2
        # Check Manhattan distance
        if abs(i1 - i2) + abs(j1 - j2) != 1:
            mismatched.append((p1, p2))

    # If no mismatched pairs, return 0
    if not mismatched:
        return 0

    def manhattan(a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    def can_swap_fix(pair1, pair2):
        # pair1 = (p1, p2), pair2 = (p3, p4)
        p1, p2 = pair1
        p3, p4 = pair2

        # Check if swapping shoes at p1 and p3 makes both pairs adjacent
        # After swap: shoe from p1 is at p3, shoe from p3 is at p1
        # So pair1 becomes (p3, p2) and pair2 becomes (p1, p4)
        if manhattan(p3, p2) == 1 and manhattan(p1, p4) == 1:
            return True

        # Check if swapping shoes at p1 and p4 makes both pairs adjacent
        # After swap: pair1 becomes (p4, p2) and pair2 becomes (p3, p1)
        if manhattan(p4, p2) == 1 and manhattan(p3, p1) == 1:
            return True

        # Check if swapping shoes at p2 and p3 makes both pairs adjacent
        # After swap: pair1 becomes (p1, p3) and pair2 becomes (p2, p4)
        if manhattan(p1, p3) == 1 and manhattan(p2, p4) == 1:
            return True

        # Check if swapping shoes at p2 and p4 makes both pairs adjacent
        # After swap: pair1 becomes (p1, p4) and pair2 becomes (p3, p2)
        if manhattan(p1, p4) == 1 and manhattan(p3, p2) == 1:
            return True

        return False

    # Build adjacency list for conflict graph
    adj = [[] for _ in range(len(mismatched))]
    for i in range(len(mismatched)):
        for j in range(i + 1, len(mismatched)):
            if can_swap_fix(mismatched[i], mismatched[j]):
                adj[i].append(j)
                adj[j].append(i)

    # Find maximum matching using Hungarian algorithm approach
    def dfs(u, match, visited):
        for v in adj[u]:
            if visited[v]:
                continue
            visited[v] = True

            if match[v] == -1 or dfs(match[v], match, visited):
                match[v] = u
                match[u] = v
                return True
        return False

    match = [-1] * len(mismatched)
    matching = 0

    for i in range(len(mismatched)):
        if match[i] == -1:
            visited = [False] * len(mismatched)
            if dfs(i, match, visited):
                matching += 1

    # Each mismatched pair needs 1 shoe moved normally
    # Each matching reduces the total by 1 (because we fix 2 pairs with 1 swap instead of 2 moves)
    return len(mismatched) - matching

print(solve())

