import math

def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def lcm(x, y):
    return x * y // gcd(x, y)

def count_bricks(x, y, a, b):
    l = lcm(x, y)
    return b // l - (a - 1) // l

line = input().strip()
x, y, a, b = map(int, line.split())

result = count_bricks(x, y, a, b)
print(result)

