def compute_lps(pattern):
    m = len(pattern)
    lps = [0] * m
    length = 0
    i = 1
    while i < m:
        if pattern[i] == pattern[length]:
            length += 1
            lps[i] = length
            i += 1
        else:
            if length != 0:
                length = lps[length - 1]
            else:
                lps[i] = 0
                i += 1
    return lps

def kmp_automaton(pattern):
    m = len(pattern)
    if m == 0:
        return [[0] * 26 for _ in range(1)]

    lps = compute_lps(pattern)
    automaton = [[0] * 26 for _ in range(m + 1)]

    for i in range(m + 1):
        for c in range(26):
            ch = chr(ord('A') + c)
            if i < m and pattern[i] == ch:
                automaton[i][c] = i + 1
            else:
                if i > 0:
                    automaton[i][c] = automaton[lps[i - 1]][c]
                else:
                    automaton[i][c] = 0

    return automaton

def solve():
    s1 = input().strip()
    s2 = input().strip()
    virus = input().strip()

    n1, n2, nv = len(s1), len(s2), len(virus)

    if nv == 0:
        # Standard LCS
        dp = [[0] * (n2 + 1) for _ in range(n1 + 1)]
