import sys

def is_lucky(num):
    s = str(num)
    return all(c in '47' for c in s)

def get_lucky_numbers_up_to(n):
    lucky = []
    # Generate all lucky numbers up to n
    candidates = ['4', '7']
    while candidates:
        next_candidates = []
        for candidate in candidates:
            num = int(candidate)
            if num <= n:
                lucky.append(num)
                next_candidates.append(candidate + '4')
                next_candidates.append(candidate + '7')
        candidates = next_candidates
    return sorted(lucky)

def factorial(x):
    if x <= 1:
        return 1
    res = 1
    for i in range(2, x + 1):
        res *= i
    return res

def get_kth_permutation_value_at_position(n, k, pos):
    # k is 1-indexed
    k -= 1  # Convert to 0-indexed
    available = list(range(1, n + 1))

    for current_pos in range(1, n + 1):
        if not available:
            return -1

        fact = factorial(len(available) - 1) if len(available) > 1 else 1
        index = k // fact

        if index >= len(available):
            return -1

        if current_pos == pos:
            return available[index]

        available.pop(index)
        k %= fact

    return -1

def solve(n, k):
    # Check if k-th permutation exists
    if k > factorial(min(n, 20)):
        return -1

    lucky_nums = get_lucky_numbers_up_to(n)

    count = 0
    for pos in lucky_nums:
        if pos > n:
            continue
        value = get_kth_permutation_value_at_position(n, k, pos)
        if value != -1 and is_lucky(value):
            count += 1

    return count

# Read input
