def count_camel_polylines(n, t):
    count = 0

    # Generate all possible y-coordinate sequences
    for y_seq in product(range(1, 5), repeat=n):
        # Check for horizontal segments
        valid = True
        for i in range(n - 1):
            if y_seq[i] == y_seq[i + 1]:
                valid = False
                break

        if not valid:
            continue

        # Count humps (local maxima) and valleys (local minima)
        humps = 0
        valleys = 0

        for j in range(1, n - 1):
            prev_y, curr_y, next_y = y_seq[j-1], y_seq[j], y_seq[j+1]
            if prev_y < curr_y > next_y:
                humps += 1
            elif prev_y > curr_y < next_y:
                valleys += 1

        # Check if the sequence represents a camel with exactly t humps and t-1 valleys
        if humps == t and valleys == t - 1:
            count += 1

    return count

