import sys

def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def lcm(a, b):
    return a * b // gcd(a, b)

def get_lcm_of_digits(digits):
    if not digits:
        return 1
    result = digits[0]
    for i in range(1, len(digits)):
        result = lcm(result, digits[i])
    return result

