def is_valid_email(email):
    # Check if it's a valid email address
    if email.count('@') != 1:
        return False
    if email.startswith('.') or email.endswith('.'):
        return False
    if email.startswith('@') or email.endswith('@'):
        return False
    # Check that email only contains valid characters
    for c in email:
        if c not in '.@' and not ('a' <= c <= 'z'):
            return False
    return True

def generate_possibilities(s, pos, current, results):
    # Generate all possible ways to parse the string
    if pos == len(s):
        if is_valid_email(current):
            results.append(current)
        return

    # Pruning: if we already have too many @ symbols, stop
    if current.count('@') > 1:
        return

    # Try taking single character (if it's a valid character)
    if s[pos] in '.@abcdefghijklmnopqrstuvwxyz':
        generate_possibilities(s, pos + 1, current + s[pos], results)

    # Try matching "dot"
    if pos + 2 < len(s) and s[pos:pos+3] == "dot":
        generate_possibilities(s, pos + 3, current + ".", results)

    # Try matching "at"
    if pos + 1 < len(s) and s[pos:pos+2] == "at":
        generate_possibilities(s, pos + 2, current + "@", results)

def solve(s):
    results = []
    generate_possibilities(s, 0, "", results)

    if not results:
        return ""

    # Find the shortest, and among those, the lexicographically smallest
    min_length = min(len(result) for result in results)
    candidates = [result for result in results if len(result) == min_length]
    return min(candidates)

input_str = input().strip()
print(solve(input_str))

