def reflect(n):
    s = str(n)
    reflected = ''.join(str(9 - int(d)) for d in s)
    return int(reflected)

def weight(n):
    return n * reflect(n)

def get_all_nines(length):
    """Generate a number with all 9s of given length"""
    if length == 0:
        return 0
    return int('9' * length)

def get_all_zeros(length):
    """Generate a number with all 0s of given length (returns 0)"""
    return 0

def generate_candidates(l, r):
    candidates = set()

    # Always include boundaries
    candidates.add(l)
    candidates.add(r)

    # Generate candidates around significant points
    str_l, str_r = str(l), str(r)
    min_len, max_len = len(str_l), len(str_r)

    # Check all 9s numbers of relevant lengths
    for length in range(min_len, max_len + 2):
        all_nines = get_all_nines(length)
        if l <= all_nines <= r:
            candidates.add(all_nines)
        # Also check one less than all 9s
        if length > 0:
            almost_all_nines = all_nines - 1
            if l <= almost_all_nines <= r:
                candidates.add(almost_all_nines)

    # Check powers of 10 and nearby numbers
    for length in range(min_len, max_len + 2):
        if length == 0:
            pow_10 = 1
        else:
            pow_10 = 10 ** (length - 1)

        # Check power of 10 and nearby values
        for offset in range(-10, 11):
            candidate = pow_10 + offset
            if l <= candidate <= r:
                candidates.add(candidate)

        # Check one less than next power of 10
        if length > 0:
            next_pow = 10 ** length
            for offset in range(-10, 1):
                candidate = next_pow + offset
                if l <= candidate <= r:
                    candidates.add(candidate)

    # For small ranges, check all numbers
    if r - l <= 1000:
        for i in range(l, r + 1):
            candidates.add(i)
    else:
        # For larger ranges, sample strategically
        # Check around boundaries more thoroughly
        for base in [l, r]:
            for offset in range(-50, 51):
                candidate = base + offset
                if l <= candidate <= r:
                    candidates.add(candidate)

        # Sample the range
        step = max(1, (r - l) // 1000)
        for i in range(l, r + 1, step):
            candidates.add(i)

        # Add some intermediate points
        mid = (l + r) // 2
        for offset in range(-100, 101):
            candidate = mid + offset
            if l <= candidate <= r:
                candidates.add(candidate)

    return list(candidates)

def solve(l, r):
    candidates = generate_candidates(l, r)

    max_weight = 0
    for n in candidates:
        if l <= n <= r:
            w = weight(n)
            max_weight = max(max_weight, w)

    return max_weight

