def can_form_ticks(n, m, k, grid):
    # Create a copy of the grid to mark processed cells
    processed = [[False for _ in range(m)] for _ in range(n)]

    for i in range(n):
        for j in range(m):
            if grid[i][j] == '*' and not processed[i][j]:
                # Try to find a tick with this cell as the center
                max_d = min(i, (n - 1 - i), j, (m - 1 - j))
                found = False
                for d in range(max_d, k - 1, -1):
                    # Check if the pattern for this d is valid
                    valid = True
                    for h in range(d + 1):
                        if i - h < 0 or j - h < 0 or j + h >= m:
                            valid = False
                            break
                        if grid[i - h][j - h] != '*' or grid[i - h][j + h] != '*':
                            valid = False
                            break
                    if valid:
                        # Mark all cells in this tick as processed
                        for h in range(d + 1):
                            processed[i - h][j - h] = True
                            processed[i - h][j + h] = True
                        found = True
                        break
                if not found:
                    return "NO"
    return "YES"

def main():
    t = int(input())
    for _ in range(t):
        n, m, k = map(int, input().split())
        grid = [input().strip() for _ in range(n)]
        print(can_form_ticks(n, m, k, grid))

