def can_erase(s):
    stack = []
    for char in s:
        if char == 'B':
            stack.append(char)
        elif char == 'A':
            if stack and stack[-1] == 'B':
                stack.pop()
        elif char == 'C':
            if stack and stack[-1] == 'B':
                stack.pop()
    return len(stack) == 0

t = int(input())
for _ in range(t):
    s = input().strip()
    if can_erase(s):
        print("YES")
    else:
        print("NO")
